import React, { userReducer, useState, useEffect } from 'react';
import { inputdata } from '../input/createuser.js';
import { Formik } from 'formik';


const input = inputdata;
let submitText = "Send request";

let setType = "";
let fields;
let builder = {};

const Page = () => {


    setType = inputdata.method;

    switch (inputdata.method) {
        case "GET":
            fields = [
                {
                    name: 'id',
                    type: 'text',
                }
            ];
            break;
        case "POST":
            fields = input.body;
            break;
        case "PUT":
            break;
        case "DELETE":
            break;

    }

    return (
        <div>
            <h1>{input.title}</h1>
            {input.method}
            <br></br>
            <br></br>
            Base URL: {input.url}
            <br></br>
            <br></br>
            <Form body={fields} />
            <br></br>
            <br></br>


        </div>
    )



}


const Form = (props) => {
    const [isShown, setIsShown] = useState(false);
    const [storedValues, setState] = useState({}); 

    function handleChange(e) {
        console.log(e.target.value);
        const name = e.target.name;
        const value = e.target.value;
        setState({
            ...storedValues,
            [name]: value

        });
        console.log("CHECK IT");
        console.log(storedValues);

    }

    const handleClick = event => {
        setIsShown(true);
    };
    const handleReset = event => {
        // setIsShown(false);
        console.log("stored values");
    console.log(storedValues);
    };


    return (


        [<h2 key="title"> Body </h2>,
        props.body.map((field, index) => (

            [<h3 key={index + "header"}> {field.name} </h3>,
            <input {...field} key={index} onChange={handleChange}></input>]
        )),
        <br></br>,
        <br></br>,
        <button onClick={handleClick}>{submitText}</button>,
        <button onClick={handleReset}>{'Reset'}</button>,
        <br></br>,
        <div style={{ backgroundColor: 'lightblue' }}>
            {isShown && <Output />}
        </div>]
    )

}


const Output = (props) => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    // Note: the empty deps array [] means
    // this useEffect will run once
    // similar to componentDidMount()
    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/posts")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    if (error) {
        return <div>Error: {error.message}</div>;
    }
    else if (!isLoaded) {
        return <div>Loading response...</div>;
    }
    else {
        return (
            <div><pre>{JSON.stringify(items, null, 2)}</pre></div>
        );
    }
};

export default Page;
