import React, { useRef, useState, useEffect } from 'react';
import { inputdata } from '../input/createuser.js';


const input = inputdata;
let submitText = "Send request";
let setType = inputdata.method;
let fields;

const Page = () => {


    switch (inputdata.method) {
        case "GET":
            fields = [
                {
                    name: 'id',
                    type: 'text',
                }
            ];
            break;
        case "POST":
            fields = input.body;
            break;
        case "PUT":
            fields = inputdata.body;
            break;
        case "DELETE":
            fields = [];
            break;

    }

    return (
        <div>
           <h1>API Endpoint Explorer - <b>{input.title}</b></h1>
            Type: {input.method}
            <br></br>
            <br></br>
            Base URL: {input.url}
            <br></br>
            <br></br>
            <br></br>


            <h2 key="title"> Body Parameters - Any relevant parms will be displayed below:</h2>
            Required fields are marked with * .
            <hr key="title2"></hr>
            <br></br>
            <Form body={fields} />
            <br></br>
            <br></br>


        </div>
    )



}


const Form = (props) => {
    const [isShown, setIsShown] = useState(false);
    const [storedValues, setState] = useState({});
    const [update, setUpdate] = useState(0);


    function handleChange(e) {
        const name = e.target.name;
        let value = e.target.value;

        //regex changes values back to integer
        if (/^\d+$/.test(value)) {
            value = parseInt(value);
        }

        if (storedValues[name] !== undefined && value === '') {
            delete storedValues[name];
        }
        else {
            setState({
                ...storedValues,
                [name]: value

            });

        }


    }

    const handleClick = event => {
        setIsShown(true);
        setUpdate(e => e + 1);
    };
    const handleReset = event => {
        setIsShown(false);
        let allfields = document.getElementsByClassName("input-field");
        for (let field of allfields) {
            field.value = null;
        }
        setState({});
    };

    return (

        [props.body.map((field, index) => (
            [<h3 key={index + "header"}> {field.name} {field.required === true ? '*' : ''} </h3>,
            <input className="input-field" {...field} key={index} onChange={handleChange}></input>]
        )),
        <hr key="title3"></hr>,
        <button key="Submit" onClick={handleClick}>{submitText}</button>,
        <button key="Reset" onClick={handleReset}>{'Reset'}</button>,
        <br key="break"></br>,
        <div key="display" style={{ backgroundColor: 'lightblue' }}>
            {isShown && <Output key={update} quickUpdate={update} userData={storedValues} />}
        </div>]
    )

}


const Output = (props) => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    // console.log(props);
    let url = inputdata.url;
    let options = {};

    switch (inputdata.method) {
        case "GET":
            url += "?";
            if (props.userData === {}){
                url = inputdata.url;
            }

            for (let attribute in props.userData) {
                url += attribute;
                url += ("=");
                url += props.userData[attribute];
            }
            // console.log(url);
            options = {};
            console.log(props.userData);
            break;
        case "POST":
            if (props.userData === {}) {
                break;
            }
            options = {
                method: 'PUT',
                body: JSON.stringify({
                    id: 1,
                    title: 'foo',
                    body: 'bar',
                    userId: 1,
                }),
                headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                },
            }

            break;
        case "PUT":
            if (props.userData === {}) {
                break;
            }
            options = {
                method: 'POST',
                body: JSON.stringify({
                    id: 1,
                    title: 'foo',
                    body: 'bar',
                    userId: 1,
                }),
                headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                },
            }
            break;
        case "DELETE":
            options = {
                method: 'DELETE',
            }
            break;

    }

    useEffect(() => {
        fetch(url, options)
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])


    if (error) {
        return <div>Error: {error.message}</div>;
    }
    else if (!isLoaded) {
        return <div>Loading response...</div>;
    }
    else {
        return (
            <div><pre>{JSON.stringify(items, null, 2)}</pre></div>
        );
    }
};

export default Page;
