import React, { useRef, useState, useEffect } from 'react';
import { inputdata } from '../input/getposts.js';


const input = inputdata;
let submitText = "Send request";
let setType = inputdata.method;
let fields;

const Page = () => {


    switch (inputdata.method) {
        case "GET":
            fields = [
                {
                    name: 'id',
                    type: 'text',
                }
            ];
            break;
        case "POST":
            fields = input.body;
            break;
        case "PUT":
            break;
        case "DELETE":
            break;

    }

    return (
        <div>
            <h1>{input.title}</h1>
            {input.method}
            <br></br>
            <br></br>
            Base URL: {input.url}
            <br></br>
            <br></br>
            <Form body={fields} />
            <br></br>
            <br></br>


        </div>
    )



}


const Form = (props) => {
    const [isShown, setIsShown] = useState(false);
    const [storedValues, setState] = useState({});
    const [update, setUpdate] = useState(0);
    const childState = useRef();


    function handleChange(e) {
        const name = e.target.name;
        const value = e.target.value;
        if (storedValues[name] !== undefined && value === "") {
            delete storedValues[name];
        }
        else {
            setState({
                ...storedValues,
                [name]: value

            });
        }


    }

    const handleClick = event => {
        setIsShown(true);
        setUpdate(e => e + 1);
    };
    const handleReset = event => {
        setIsShown(false);
        let allfields = document.getElementsByClassName("input-field");
        for (let field of allfields) {
            field.value = "";
        }
    };

    return (

        [<h2 key="title"> Body </h2>,

        props.body.map((field, index) => (

            [<h3 key={index + "header"}> {field.name} </h3>,
            <input className="input-field" {...field} key={index} onChange={handleChange}></input>]
        )),
        <br key="break2" ></br>,
        <button key="Submit" onClick={handleClick}>{submitText}</button>,
        <button key="Reset" onClick={handleReset}>{'Reset'}</button>,
        <br key="break"></br>,
        <div key="display" style={{ backgroundColor: 'lightblue' }}>
            {isShown && <Output key={update} ref={childState} userData={storedValues} />}
        </div>]
    )

}


const Output = (props) => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    console.log(props);
    let url = "";

    switch (inputdata.method) {
        case "GET":
            url += inputdata.url;
            url += "?";

            for (let attribute in props.userData) {
                url += attribute;
                url += ("=");
                url += props.userData[attribute];
            }
            console.log(url);
            break;
        case "POST":

            break;
        case "PUT":
            break;
        case "DELETE":
            break;

    }

    useEffect(() => {
        fetch(url)
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    if (error) {
        return <div>Error: {error.message}</div>;
    }
    else if (!isLoaded) {
        return <div>Loading response...</div>;
    }
    else {
        return (
            <div><pre>{JSON.stringify(items, null, 2)}</pre></div>
        );
    }
};

export default Page;
